+ db = FirebaseFirestore.getInstance();
+
+ // Lấy dữ liệu sản phẩm từ Intent
+ String productId = getIntent().getStringExtra("PRODUCT_ID");
+ if (productId == null) {
+ Toast.makeText(this, "Không tìm thấy thông tin sản phẩm", Toast.LENGTH_SHORT).show();
+ finish();
+ return;
