 Category category = new Category();
 category.setName(categoryName.trim());
 category.setIconUrl("");

 db.collection(COLLECTION_CATEGORIES)
 .add(category)
 .addOnSuccessListener(documentReference -> {
 callback.onSuccess();
 })
 .addOnFailureListener(e -> {
 callback.onError("Lỗi: " + e.getMessage());
 });
 })
 .addOnFailureListener(e -> {
 callback.onError("Lỗi: " + e.getMessage());
 });
 }

